mapper.update:
update message
set text = #{text,jdbcType=VARCHAR},
timestamp = #{timestamp,jdbcType=VARCHAR},
roomId = #{roomId,jdbcType=INTEGER},
username = #{username}
where id = #{id}

mapper.select:
select id, text, timestamp, roomId, username
from message
where id = #{id}

select id, text, timestamp
from message
where roomId = #{roomId}
order by id desc
LIMIT #{pageIndex},#{pageSize};
